import { useState, useEffect } from 'react';
import { ethers } from 'ethers';
import detectEthereumProvider from '@metamask/detect-provider';
import SimpleContractABI from './contracts/SimpleContract.json';

const contractAddress = '0x821f3361D454cc98b7555221A06Be563a7E2E0A6';
const contract = new ethers.Contract(contractAddress, SimpleContractABI.abi, provider);
const App = () => {
  const [provider, setProvider] = useState(null);
  const [walletAddress, setWalletAddress] = useState('');
  const [balance, setBalance] = useState(0);

  useEffect(() => {
    const initializeProvider = async () => {
      const provider = await detectEthereumProvider();

      if (provider) {
        setProvider(provider);
        subscribeToAccountsChanges(provider);
      } else {
        console.error('Please install Metamask to interact with the wallet.');
      }
    };

    initializeProvider();
  }, []);

  const subscribeToAccountsChanges = (provider) => {
    provider.on('accountsChanged', (accounts) => {
      setWalletAddress(accounts[0]);
      getBalance(); // Fetch balance whenever the connected account changes
    });
  };

  const connectWallet = async () => {
    try {
      const accounts = await provider.request({ method: 'eth_requestAccounts' });
      setWalletAddress(accounts[0]); // Set the wallet address after connecting
    } catch (error) {
      console.error('Error connecting to the wallet:', error);
    }
  };

  const getBalance = async () => {
    if (walletAddress && provider) {
      const web3Provider = new ethers.providers.Web3Provider(provider); // Use Web3Provider
      const balance = await web3Provider.getBalance(walletAddress); // Use web3Provider.getBalance
      setBalance(ethers.utils.formatEther(balance));
    }
  };

  return (
    <div className="App">
      <h1>Metamask Wallet Integration</h1>
      <p>Wallet Address: {walletAddress}</p>
      <p>Balance: {balance} ETH</p>
      <button onClick={connectWallet}>Connect Wallet</button>
      <button onClick={getBalance}>Get Balance</button>
      {/* Add other transaction functions and UI elements */}
    </div>
  );
};

export default App;
