import React, { useState, useEffect } from 'react';
import './App.css';
import { ethers } from 'ethers';
import SimpleContractABI from './contracts/SimpleContract.json';

const App = () => {
  const [contractValue, setContractValue] = useState('');
  const [newValue, setNewValue] = useState('');
  const [contractOwner, setContractOwner] = useState('');
  const [contractBalance, setContractBalance] = useState(0);

  const provider = new ethers.providers.JsonRpcProvider('http://localhost:8545');
  const contractAddress = '0x49fd2BE640DB2910c2fAb69bB8531Ab6E76127ff';
  const contract = new ethers.Contract(contractAddress, SimpleContractABI.abi, provider);

  const getContractValue = async () => {
    const value = await contract.getValue();
    setContractValue(value.toString());
  };

  const getContractOwner = async () => {
    const owner = await contract.owner();
    setContractOwner(owner);
  };

  const getContractBalance = async () => {
    const balance = await provider.getBalance(contractAddress);
    setContractBalance(ethers.utils.formatEther(balance));
  };

  useEffect(() => {
    getContractValue();
    getContractOwner();
    getContractBalance();
  }, []);

  const handleSetValue = async () => {
    try {
      if (window.ethereum) {
        const accounts = await window.ethereum.request({ method: 'eth_requestAccounts' });
        const signer = provider.getSigner(accounts[0]);
        const contractWithSigner = contract.connect(signer);

        await contractWithSigner.setValue(newValue);
        getContractValue();
      } else {
        alert('MetaMask is not installed or not accessible.');
      }
    } catch (error) {
      console.error('Error sending transaction:', error);
    }
  };

  const handleIncrementValue = async () => {
    try {
      if (window.ethereum) {
        const accounts = await window.ethereum.request({ method: 'eth_requestAccounts' });
        const signer = provider.getSigner(accounts[0]);
        const contractWithSigner = contract.connect(signer);

        await contractWithSigner.incrementValue();
        getContractValue();
      } else {
        alert('MetaMask is not installed or not accessible.');
      }
    } catch (error) {
      console.error('Error sending transaction:', error);
    }
  };

  const handleDecrementValue = async () => {
    try {
      if (window.ethereum) {
        const accounts = await window.ethereum.request({ method: 'eth_requestAccounts' });
        const signer = provider.getSigner(accounts[0]);
        const contractWithSigner = contract.connect(signer);

        await contractWithSigner.decrementValue();
        getContractValue();
      } else {
        alert('MetaMask is not installed or not accessible.');
      }
    } catch (error) {
      console.error('Error sending transaction:', error);
    }
  };

  const handleDonateEther = async () => {
    try {
      if (window.ethereum) {
        const accounts = await window.ethereum.request({ method: 'eth_requestAccounts' });
        const signer = provider.getSigner(accounts[0]);
        const contractWithSigner = contract.connect(signer);

        // Let's send 0.1 Ether as a donation (can be customized as needed)
        const donationAmount = ethers.utils.parseEther('0.1');
        await contractWithSigner.donate({ value: donationAmount });
        getContractBalance();
      } else {
        alert('MetaMask is not installed or not accessible.');
      }
    } catch (error) {
      console.error('Error sending transaction:', error);
    }
  };

  const handleWithdrawEther = async () => {
    try {
      if (window.ethereum) {
        const accounts = await window.ethereum.request({ method: 'eth_requestAccounts' });
        const signer = provider.getSigner(accounts[0]);
        const contractWithSigner = contract.connect(signer);

        // Check if the sender is the contract owner before allowing the withdrawal
        const owner = await contract.owner();
        if (accounts[0] !== owner) {
          alert('You are not the contract owner. Only the owner can withdraw Ether.');
          return;
        }

        await contractWithSigner.withdraw();
        getContractBalance();
      } else {
        alert('MetaMask is not installed or not accessible.');
      }
    } catch (error) {
      console.error('Error sending transaction:', error);
    }
  };

  return (
    <div className="App">
      <h1>SimpleContract Frontend</h1>
      <p>Contract Value: {contractValue}</p>
      <p>Contract Owner: {contractOwner}</p>
      <p>Contract Balance: {contractBalance} Ether</p>
      <div>
        <input type="number" value={newValue} onChange={(e) => setNewValue(e.target.value)} />
        <button onClick={handleSetValue}>Set Value</button>
      </div>
      <button onClick={handleIncrementValue}>Increment Value</button>
      <button onClick={handleDecrementValue}>Decrement Value</button>
      <button onClick={handleDonateEther}>Donate Ether</button>
      <button onClick={handleWithdrawEther}>Withdraw Ether (Owner Only)</button>
    </div>
  );
};

export default App;
