import React, { useState, useEffect } from 'react';
import './App.css';
import Web3 from 'web3'; // Import Web3 library
import SimpleContractABI from './contracts/SimpleContract.json';

const App = () => {
  const [contractValue, setContractValue] = useState('');
  const [newValue, setNewValue] = useState('');
  const [walletBalance, setWalletBalance] = useState('');

  const provider = new Web3.providers.HttpProvider('http://localhost:8545'); // Update the URL to your local network
  const web3 = new Web3(provider);

  const contractAddress = ' 0x821f3361D454cc98b7555221A06Be563a7E2E0A6'; // Update with your contract address
  const contract = new web3.eth.Contract(SimpleContractABI.abi, contractAddress);

  const getContractValue = async () => {
    const value = await contract.methods.getValue().call();
    setContractValue(value);
  };

  const getWalletBalance = async () => {
    const accounts = await web3.eth.getAccounts();
    if (accounts.length > 0) {
      const balance = await web3.eth.getBalance(accounts[0]);
      setWalletBalance(web3.utils.fromWei(balance, 'ether'));
    }
  };

  useEffect(() => {
    getContractValue();
    getWalletBalance();
  }, []);

  const handleSetValue = async () => {
    // ... Same as before ...
  };

  const handleIncrementValue = async () => {
    try {
      if (window.ethereum) {
        await window.ethereum.enable();
        const accounts = await web3.eth.getAccounts();
        if (accounts.length > 0) {
          const gasPrice = await web3.eth.getGasPrice();
          const contractWithSigner = contract.clone();
          contractWithSigner.options.from = accounts[0];
          contractWithSigner.options.gasPrice = gasPrice;
          contractWithSigner.options.gas = 3000000;

          const valueToIncrement = 1; // You can customize the value to increment
          const tx = contractWithSigner.methods.incrementValue().send({
            value: web3.utils.toWei(valueToIncrement.toString(), 'ether'), // Send the amount of Ether along with the transaction
          });

          // You can listen for the transaction confirmation here if needed
          tx.on('confirmation', (confirmationNumber, receipt) => {
            // Transaction confirmed, do something if needed
          });

          getContractValue();
          getWalletBalance();
        } else {
          alert('No accounts found in MetaMask.');
        }
      } else {
        alert('MetaMask is not installed or not accessible.');
      }
    } catch (error) {
      console.error('Error sending transaction:', error);
    }
  };

  const handleDecrementValue = async () => {
    // ... Same as before ...
  };

  return (
    <div className="App">
      <h1>SimpleContract Frontend</h1>
      <p>Contract Value: {contractValue}</p>
      <p>Wallet Balance: {walletBalance} ETH</p>
      <div>
        <input type="number" value={newValue} onChange={(e) => setNewValue(e.target.value)} />
        <button onClick={handleSetValue}>Set Value</button>
      </div>
      <button onClick={handleIncrementValue}>Increment Value</button>
      <button onClick={handleDecrementValue}>Decrement Value</button>
    </div>
  );
};

